def is_armstrong_string(num):
    """Checks if a number is an Armstrong number using string manipulation."""
    num_str = str(num)
    num_digits = len(num_str)
    sum_of_powers = sum(int(digit) ** num_digits for digit in num_str)
    return sum_of_powers == num

# Example usage
